var commands={commandList:[],lastError:"",addCommand:function(m,n,o,a){commands.commandList.push({name:m,altname:n,description:o,command:a})},runCommand:function(m){const n=commands.getCommand(m);try{null!==n&&n.command()}catch(m){commands.lastError=m,console.log(m)}},getCommand:function(m){return commands.commandList.find(n=>n.name===m)},getAltCommand:function(m){return commands.commandList.find(n=>n.altname===m)},listCommands:function(){return commands.commandList.map(m=>({name:m.name,description:m.description}))},removeCommand:function(m){commands.commandList=commands.commandList.filter(n=>n.name!==m)}};export default commands;