var filesystems={fileSystemList:[],addFileSystem:function(e,s,t){filesystems.fileSystemList.push({name:e,description:s,fileSystem:t})},getFileSystem:function(e){return filesystems.fileSystemList.find(e=>{e.name,com})},listFileSystems:function(){return filesystems.fileSystemList.map(e=>({name:e.name,description:e.description}))},removeFileSystem:function(e){filesystems.fileSystemList=filesystems.fileSystemList.filter(s=>s.name!=e)}};export default filesystems;